import java.util.LinkedList;


public class LinkedList {
    private Node head;


    public LinkedList() {
        head = null;
    }


    public void add(int data) {
        Node newNode = new Node(data);
        if (head == null) {
            head = newNode;
        } else {
            Node current = head;
            while (current != null) {
                current = current.next;
            }
            current = newNode;
        }
    }


    public void remove(int data) {
        Node current = head;
        Node prev = null;
        while (current != null && current.data != data) {
            prev = current;
            current = current.next;
        }
        if (current != null) {
            prev.next = current.next;
            current.next = null;
        }
    }


    public void print() {
        Node current = head;
        while (current != null) {
            System.out.print(current.data + " ");
            current = current.next;
        }
    }


    private class Node {
        private int data;
        private Node next;


        public Node(int data) {
            this.data = data;
            next = null;
        }
    }
}

